import "core-js/modules/es6.array.iterator";
import "core-js/modules/es6.promise";
import Vue from 'vue'; //引入node-module的Vue模块

import Router from 'vue-router'; //引入node-module的router模块
//引入组件到相应的路径名变量

import Home from "./components/weixin/Home.vue";
import MessageList from "./components/weixin/common/MessageList.vue";
import Login from "./components/xz/Login.vue";
import product from "./components/xz/product.vue";
import XZHome from "./components/xz/Home.vue";
import XZCart from "./components/xz/common/Cart.vue";
import bmhome from "./components/bamobile/Bama.vue";
import cart from "./components/bamobile/common/cart.vue";
import user from "./components/bamobile/common/user.vue";
import detail from "./components/bamobile/common/detail.vue";
import prolist from "./components/bamobile/common/prolist.vue"; //使用路由配置组件的访问路径

Vue.use(Router);
export default new Router({
  routes: [{
    path: '/XZHome',
    component: XZHome
  }, {
    path: '/product',
    component: product
  }, {
    path: '/Login',
    component: Login
  }, {
    path: '/MessageList',
    component: MessageList
  }, {
    path: '/Home',
    component: Home
  }, {
    path: '/',
    redirect: "/bama"
  }, {
    path: '/xzCart',
    component: XZCart
  }, {
    path: '/bama',
    component: bmhome
  }, {
    path: '/cart',
    component: cart
  }, {
    path: '/user',
    component: user
  }, {
    path: '/detail',
    component: detail
  }, {
    path: '/prolist',
    component: prolist
  }]
});
